class WineryListTableViewController: UITableViewController {    var wineryListArray = [Wineries]()    let wineDAO:WineryDAO = WineryDAO()        func loadWineList(){        wineryListArray = wineDAO.selectWineriesList()    }    override func viewDidLoad() {        super.viewDidLoad()        loadWineList()}override func numberOfSections(in tableView: UITableView) -> Int { 	return 1    }override func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {        return wineryListArray.count    }    override func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {        let cell = tableView.dequeueReusableCell(withIdentifier: "WineryCellTableViewCell", for: indexPath) as! WineryCellTableViewCell        let winery:Wineries = wineryListArray[(indexPath as NSIndexPath).row]                      cell.wineryNameOutlet.text = winery.name        cell.regionOutlet.text = winery.region        cell.countryOutlet.text = winery.country        cell.volumeOutlet.text = String(winery.volume)        cell.uomOutlet.text = winery.uom         return cell    }