func selectAudioWithPlayback(_ selectedFile:String)->Data{               var audio:Data = Data()        let sql:String = "SELECT audioData FROM audios wherefileName= \(selectedFile)"                        if(sqlite3_open(dbPath.path!, &db)==SQLITE_OK){            if(sqlite3_prepare_v2(db, sql, -1, &sqlStatement, nil)==SQLITE_OK){                while (sqlite3_step(sqlStatement)==SQLITE_ROW) {                    let raw:UnsafePointer = sqlite3_column_blob(sqlStatement, 3);                    let rawLen:Int32 = sqlite3_column_bytes(sqlStatement, 3);                    audio = Data(bytes: raw, count: Int(rawLen))                }            }            sqlite3_finalize(sqlStatement);            sqlite3_close(db);            return audio        }    }