func backupCopyDatabase(){        if sqlite3_close(db) == SQLITE_OK{            let fileMgr = FileManager.default                        do {                try fileMgr.copyItem(atPath: self.sourcedb, toPath: self.targetdb)                let deleteSQL = "DELETE FROM main.table"                if(sqlite3_exec(db, deleteSQL, nil, sqlStatement, err) == SQLITE_OK){                    if(sqlite3_exec(db, "VACUUM", nil, &sqlStatement, &err) == SQLITE_OK){                        print("database compressed")                    }                })             }            catch let error as NSError {                print("Backup error: \(error)")            }                    }            }